import React, { useEffect, useState } from 'react';
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';
import Button from '@mui/material/Button';
import Paper from '@mui/material/Paper';
import Grid from '@mui/material/Grid';
import { useParams, useNavigate } from 'react-router-dom';
import { useSelector } from 'react-redux';
import { postNewEntry } from '../api.js';
import { DateTimePicker } from '@mui/lab';

function useHandleVisitor(username) {
  const navigate = useNavigate();
  const name = useSelector((state) => state.login.name);

  useEffect(() => {
    if (name !== username || name == null) {
      navigate('/');
    }
  }, [name, username, navigate]);
}

export default function EditJournal() {
  const { username } = useParams();
  const email = useSelector((state) => state.login.email);
  useHandleVisitor(username);

  const [text, setText] = useState('');
  const [title, setTitle] = useState('');
  const [images, setImages] = useState([]);
  const [openDate, setOpenDate] = useState('');

  const handleTextChange = (event) => {
    setText(event.target.value);
  };

  const handleTitleChange = (event) => {
    setTitle(event.target.value);
  };

  const handleImageUpload = (event) => {
    const file = event.target.files[0];
    const reader = new FileReader();

    reader.onload = (e) => {
      setImages((prevImages) => [...prevImages, e.target.result]);
    };

    reader.readAsDataURL(file);
  };

  const handleClearImages = () => {
    setImages([]);
  };

  const handleSaveJournalEntry = async () => {
    try {
      const encodedImages = images.map((image) => {
        const base64Data = image.split(',')[1]; // Extract base64 data from the image URL
        return base64Data;
      });

      const entryData = {
        email: email,
        text_content: text,
        images: encodedImages,
        open_date: openDate,
        entry_title: title, // Use the title value from the state
      };

      const response = await postNewEntry(entryData);
      console.log('Post New Entry Response:', response);
    } catch (error) {
      console.error('Post New Entry Error:', error.message);
    }
  };

  return (
    <Box
      sx={{
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        minHeight: '100vh',
        padding: '2rem',
        background: 'white', // Set the surrounding area background color to white
      }}
    >
      <Paper
        sx={{
          padding: '2rem',
          width: '100%',
          maxWidth: '600px',
          background: 'lightgrey', // Set the container background color to light grey
        }}
      >
        <Grid container spacing={2}>
          <Grid item xs={12}>
            <TextField
              label="Title"
              value={title}
              onChange={handleTitleChange}
              fullWidth
            />
          </Grid>
          <Grid item xs={12}>
            <TextField
              label="Type something"
              value={text}
              onChange={handleTextChange}
              fullWidth
              multiline
              rows={4}
            />
          </Grid>
          <Grid item xs={12}>
            <DateTimePicker
              label="Basic date time picker"
              value={openDate}
              onChange={(newValue) => setOpenDate(newValue)}
            />
          </Grid>
          <Grid item xs={12}>
            <input
              type="file"
              accept="image/*"
              onChange={handleImageUpload}
              style={{ display: 'none' }}
              id="upload-button"
            />
            <label htmlFor="upload-button">
              <Button component="span" variant="contained" color="inherit"
              sx={{
                bgcolor: '#f5f5f5', // Lighter gray background color
                color: 'black', // Dark shade of gray for the text
                borderRadius: '8px', // Rounded corners
                '&:hover': {
                  bgcolor: 'black', // Black background color on hover
                  color: 'white', // White text color on hover
                },
              }}
              >
                Upload Image
              </Button>
            </label>
            <Button
              variant="contained"
              color="inherit"
              onClick={handleClearImages}
              sx={{
                bgcolor: '#f5f5f5', // Lighter gray background color
                color: 'black', // Dark shade of gray for the text
                borderRadius: '8px', // Rounded corners
                '&:hover': {
                  bgcolor: 'black', // Black background color on hover
                  color: 'white', // White text color on hover
                },
              }}
            >
              Clear Images
            </Button>
            <Button
              variant="contained"
              color="inherit"
              onClick={handleSaveJournalEntry}
              sx={{
                bgcolor: '#f5f5f5', // Lighter gray background color
                color: 'black', // Dark shade of gray for the text
                borderRadius: '8px', // Rounded corners
                '&:hover': {
                  bgcolor: 'black', // Black background color on hover
                  color: 'white', // White text color on hover
                },
              }}
            >
              Save Journal Entry
            </Button>
          </Grid>
          <Grid item xs={12}>
            <Paper sx={{ padding: 2 }}>
              {images.map((image, index) => (
                <img
                  key={index}
                  src={image}
                  alt={`Image ${index + 1}`}
                  style={{ width: '200px', height: 'auto', marginRight: '10px',backgroundColor: 'lightgrey' }}
                />
              ))}
            </Paper>
          </Grid>
        </Grid>
      </Paper>
    </Box>
  );
}
