import React, { useState } from 'react';
import { Box, Button } from '@mui/material';
import JournalForm from './editJournalForm';

const EditJournal = () => {
  const [title, setTitle] = useState('');
  const [text, setText] = useState('');
  const [openDate, setOpenDate] = useState('');
  const [image, setImage] = useState('');
  const [showPrompt, setShowPrompt] = useState(false);

  const handleTitleChange = (e) => {
    setTitle(e.target.value);
  };

  const handleTextChange = (e) => {
    setText(e.target.value);
  };

  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    const reader = new FileReader();
    reader.onloadend = () => {
      setImage(reader.result);
    };
    if (file) {
      reader.readAsDataURL(file);
    }
  };

  const handleClearImage = () => {
    setImage('');
  };

  const handleSaveJournalEntry = () => {
    // Handle saving the journal entry
    // ...

    setShowPrompt(true);
  };

  return (
    <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
      <JournalForm
        title={title}
        text={text}
        openDate={openDate}
        image={image}
        handleTitleChange={handleTitleChange}
        handleTextChange={handleTextChange}
        handleImageUpload={handleImageUpload}
        handleClearImage={handleClearImage}
        setShowPrompt={setShowPrompt}
        handleSaveJournalEntry={handleSaveJournalEntry}
      />
      {showPrompt && (
        <Box
          sx={{
            position: 'fixed',
            top: 0,
            left: 0,
            width: '100%',
            height: '100%',
            backgroundColor: 'rgba(0, 0, 0, 0.5)',
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center',
          }}
        >
          <Box sx={{ background: 'white', padding: '1rem', borderRadius: '8px' }}>
            <Typography variant="body1" color="black">
              Your journal entry has been saved.
            </Typography>
            <Button onClick={() => setShowPrompt(false)}>Close</Button>
          </Box>
        </Box>
      )}
    </Box>
  );
};

export default EditJournal;
