import React, { useEffect, useState } from 'react';
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';
import Button from '@mui/material/Button';
import Paper from '@mui/material/Paper';
import Grid from '@mui/material/Grid';
import { useParams, useNavigate } from 'react-router-dom';
import { useSelector } from 'react-redux';
import { postNewEntry } from '../api.js';
import { DateTimePicker } from '@mui/lab';

function useHandleVisitor(username) {
  // ... unchanged code
}

export default function EditJournal() {
  // ... unchanged code

  return (
    <Box
      sx={{
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        minHeight: '100vh',
        padding: '2rem',
        background: 'white', // Set the surrounding area background color to white
      }}
    >
      <Paper
        sx={{
          padding: '2rem',
          width: '100%',
          maxWidth: '600px',
          background: 'grey', // Set the container background color to grey
        }}
      >
        <Grid container spacing={2}>
          <Grid item xs={12}>
            <TextField
              label="Title"
              value={title}
              onChange={handleTitleChange}
              fullWidth
            />
          </Grid>
          <Grid item xs={12}>
            <TextField
              label="Type something"
              value={text}
              onChange={handleTextChange}
              fullWidth
              multiline
              rows={4}
            />
          </Grid>
          <Grid item xs={12}>
            <DateTimePicker
              label="Basic date time picker"
              value={openDate}
              onChange={(newValue) => setOpenDate(newValue)}
            />
          </Grid>
          <Grid item xs={12}>
            <input
              type="file"
              accept="image/*"
              onChange={handleImageUpload}
              style={{ display: 'none' }}
              id="upload-button"
            />
            <label htmlFor="upload-button">
              <Button
                component="span"
                variant="contained"
                color="inherit"
                sx={{
                  color: 'black', // Set the button text color to black
                  background: 'grey', // Set the button background color to grey
                }}
              >
                Upload Image
              </Button>
            </label>
            <Button
              variant="contained"
              color="inherit"
              onClick={handleClearImages}
              sx={{
                color: 'black', // Set the button text color to black
                background: 'grey', // Set the button background color to grey
              }}
            >
              Clear Images
            </Button>
            <Button
              variant="contained"
              color="inherit"
              onClick={handleSaveJournalEntry}
              sx={{
                color: 'black', // Set the button text color to black
                background: 'grey', // Set the button background color to grey
              }}
            >
              Save Journal Entry
            </Button>
          </Grid>
          <Grid item xs={12}>
            <Paper sx={{ padding: 2 }}>
              {images.map((image, index) => (
                <img
                  key={index}
                  src={image}
                  alt={`Image ${index + 1}`}
                  style={{ width: '200px', height: 'auto', marginRight: '10px' }}
                />
              ))}
            </Paper>
          </Grid>
        </Grid>
      </Paper>
    </Box>
  );
}
