import React from 'react';
import Grid from '@mui/material/Grid';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';

const JournalFormImage = ({
  image,
  handleImageUpload,
  handleClearImage,
}) => {
  return (
    <>
      {image ? (
        <Grid item xs={12} sx={{ textAlign: 'center' }}>
          <img
            src={image}
            alt="Uploaded Image"
            style={{ height: '200px', width: '100%', objectFit: 'cover' }}
          />
          <Button
            variant="contained"
            color="inherit"
            onClick={handleClearImage}
            sx={{
              bgcolor: '#f5f5f5',
              color: 'black',
              borderRadius: '8px',
              marginTop: '1rem',
              '&:hover': {
                bgcolor: 'black',
                color: 'white',
              },
            }}
          >
            Clear Image
          </Button>
        </Grid>
      ) : (
        <Grid item xs={12} sx={{ textAlign: 'center' }}>
          <input
            type="file"
            accept="image/*"
            onChange={handleImageUpload}
            style={{ display: 'none' }}
            id="upload-button"
          />
          <label htmlFor="upload-button">
            <Button
              component="span"
              variant="contained"
              color="inherit"
              sx={{
                bgcolor: '#f5f5f5',
                color: 'black',
                borderRadius: '8px',
                '&:hover': {
                  bgcolor: 'black',
                  color: 'white',
                },
              }}
            >
              Upload Image
            </Button>
          </label>
        </Grid>
      )}
    </>
  );
};

export default JournalFormImage;
