import React, { useState } from "react";
import { useDispatch } from 'react-redux';
import { GoogleLogin } from '@react-oauth/google';
import { handleLoginSuccess } from '../api.js';
import { loginUser } from '../loginReducer.js';
import { useNavigate } from 'react-router-dom';
import Box from '@mui/material/Box';
import Typography from '@mui/material/Typography';

function useHandleLogin() {
  const dispatch = useDispatch();
  const navigate = useNavigate();

  async function handleLogin(credentialResponse) {
    try {
      const result = await handleLoginSuccess(credentialResponse);
      // Perform additional actions after successful login
      dispatch(loginUser({ email: result.email, name: result.name }));
      if (result) {
        navigate(`/homepage/${encodeURIComponent(result.name)}`); // Navigate to the '/homepage' route
      }
    } catch (error) {
      console.log('Login Failed:', error);
    }
  }

  return handleLogin;
}

function Login() {
  const handleLogin = useHandleLogin();
  const [showText, setShowText] = useState(false);

  const handleMouseEnter = () => {
    setShowText(true);
  };

  const handleMouseLeave = () => {
    setShowText(false);
  };

  return (
    <Box
      sx={{
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        backgroundColor: 'white',
        padding: '20px',
      }}
    >
      <Box
        sx={{
          position: 'fixed',
          top: 0,
          left: 0,
          width: '100%',
          backgroundColor: 'white',
          zIndex: 1,
          textAlign: 'center',
        }}
      >
        <Typography
          variant="h4"
          component="h1"
          gutterBottom
          onMouseEnter={handleMouseEnter}
          onMouseLeave={handleMouseLeave}
        >
          Diario
        </Typography>
        {showText && (
          <Typography
            variant="subtitle1"
            gutterBottom
            sx={{
              fontSize: 16,
              textAlign: 'left',
              marginLeft: '20px',
            }}
          >
            nlp & sentiment analysis
          </Typography>
        )}
      </Box>
      <Box
        sx={{
          marginTop: '60px',
          textAlign: 'center',
        }}
      >
        <Typography variant="subtitle1" gutterBottom>
          It's a diary, literally.
        </Typography>
        <Box
          sx={{
            backgroundColor: 'white',
            padding: '20px',
            textAlign: 'center',
            fontFamily: 'Times New Roman',
          }}
        >
          <GoogleLogin onSuccess={handleLogin} />
        </Box>
        <Typography variant="caption" color="text.secondary">
          It was that or diarryea pronounced: dai - ur -ee -uh.
        </Typography>
      </Box>
    </Box>
  );
}

export default Login;
