import React from 'react';
import { Typography, Box, Paper, Grid, TextField, Button } from '@mui/material';

const JournalForm = ({
  title,
  text,
  openDate,
  image,
  handleTitleChange,
  handleTextChange,
  handleImageUpload,
  handleClearImage,
  setShowPrompt,
  handleSaveJournalEntry,
  setOpenDate, // Add setOpenDate prop
}) => {
  const handlePromptClose = () => {
    setShowPrompt(false);
  };

  return (
    <Box
      sx={{
        padding: '2rem',
        width: '100%',
        maxWidth: '600px',
        background: 'white',
      }}
    >
      <Grid container spacing={2}>
        <Grid item xs={12}>
          <TextField
            label="Title"
            value={title}
            onChange={handleTitleChange}
            fullWidth
          />
        </Grid>
        <Grid item xs={12}>
          <TextField
            label="Write here."
            value={text}
            onChange={handleTextChange}
            fullWidth
            multiline
            rows={4}
          />
        </Grid>
        <Grid item xs={12}>
          <TextField
            type="date"
            value={openDate}
            onChange={(e) => setOpenDate(e.target.value)} // Use setOpenDate prop
            fullWidth
          />
        </Grid>
        {/* ... */}
      </Grid>
    </Box>
  );
};

export default JournalForm;
